SELECT users.id, users.creator, users.create_date, users.modifier, users.modify_date, users.state, users.name, sex, age, phone, role.name as depart_role_id, depar.name as depart_id, max_threshold FROM t_sys_users AS users LEFT JOIN t_sys_user_role AS user_role ON users.id = user_role.user_id LEFT JOIN t_sys_role as role on user_role.role_id = role.id LEFT JOIN t_sys_department as depar ON role.department_id = depar.id where users.depart_id = #{department_id}
and users.name = #{name,jdbcType=VARCHAR}
limit #{start,jdbcType=DECIMAL},#{end,jdbcType=DECIMAL}

