mapper.select:
SELECT t.id, product.name AS product_name, t.product_id, t.`number`, warn.warn_number FROM t_store as t LEFT JOIN t_sys_purchase_product AS product ON product.id = t.product_id LEFT JOIN t_store_warn AS warn ON warn.product_id = t.product_id
and product.name = #{name,jdbcType=VARCHAR}
limit #{start,jdbcType=DECIMAL},#{end,jdbcType=DECIMAL}

SELECT t.id, us. NAME AS creator, users. NAME AS check_user_id, t.create_date, t.end_date, t.begin_date, t.state, r.display_name FROM t_store_check AS t LEFT JOIN t_sys_users AS us ON t.creator = us.id LEFT JOIN t_sys_users AS users ON t.check_user_id = users.id LEFT JOIN t_sys_resource as r ON t.state = r.id
AND begin_date between #{startTime} and #{endTime}
limit #{start,jdbcType=DECIMAL},#{end,jdbcType=DECIMAL}

SELECT t.id, us.name as creator, t.create_date, t.modifier, t.modify_date, r.display_name as state, t.checkout_date, u.name as checkout_user_name, t.checkout_user_id, p.name as product_name, t.product_id, t.number FROM t_store_checkout as t LEFT JOIN t_sys_users as us ON t.creator = us.id LEFT JOIN t_sys_users as u ON t.checkout_user_id = u.id LEFT JOIN t_sys_resource as r ON t.state = r.id LEFT JOIN t_sys_purchase_product as p ON t.product_id = p.id WHERE t.state = 5
AND checkout_date between #{startTime} and #{endTime}
AND u.name = #{name,jdbcType=VARCHAR}
limit #{start,jdbcType=DECIMAL},#{end,jdbcType=DECIMAL}

SELECT t.id, us.name as creator, t.create_date, t.modifier, t.modify_date, r.display_name as state, t.put_id_date, u.name as put_in_user_name, t.put_in_user_id, p.name as product_name, t.product_id, t.put_in_number FROM t_store_put_in as t LEFT JOIN t_sys_users as us ON t.creator = us.id LEFT JOIN t_sys_users as u ON t.put_in_user_id = u.id LEFT JOIN t_sys_resource as r ON t.state = r.id LEFT JOIN t_sys_purchase_product as p ON t.product_id = p.id WHERE t.state = 5
AND put_id_date between #{startTime} and #{endTime}
AND u.name = #{name,jdbcType=VARCHAR}
limit #{start,jdbcType=DECIMAL},#{end,jdbcType=DECIMAL}
