mapper.select:
SELECT t.id, u. NAME AS user_id, product.name AS product_name, t.product_id, t.put_id_date as date, t.source_type, t.source_id, t.state FROM t_store_put_in AS t LEFT JOIN t_sys_users AS u ON t.put_in_user_id = u.id LEFT JOIN t_sys_purchase_product AS product ON product.id = t.product_id LEFT JOIN t_purchase_bill AS bill ON bill.id = t.source_id WHERE t.state = 3
and u.name = #{name,jdbcType=VARCHAR}
AND put_id_date between #{startTime} and #{endTime}

SELECT t.id, u. NAME AS user_id, product.name AS product_name, t.product_id, t.checkout_date as date, t.state FROM t_store_checkout AS t LEFT JOIN t_sys_users AS u ON t.checkout_user_id = u.id LEFT JOIN t_sys_purchase_product AS product ON product.id = t.product_id WHERE t.state = 3
and u.name = #{name,jdbcType=VARCHAR}
AND checkout_date between #{startTime} and #{endTime}
